from collections import Counter


class Solution:
    def longestPalindrome(self, s: str) -> int:
        s_count = Counter(s)
        odd_count = 0
        for ch in s_count:
            if s_count[ch] % 2 == 1:
                odd_count += 1
        if odd_count == 0:
            return len(s)
        return len(s) - odd_count + 1


if __name__ == '__main__':
    s = ""
    sol = Solution()
    expected = 981
    actual = sol.longestPalindrome(s)
    assert actual == expected
